import withNextra from 'nextra';

export default withNextra({
  theme: 'nextra-theme-docs',
  themeConfig: './theme.config.mjs',
  standalone: true
})({
  output: 'export',
  basePath: '/webizen-app-docs',
  trailingSlash: true,
  images: {
    unoptimized: true
  }
});

export default {
  logo: 'Webizen Documentation',
  project: {
    link: 'https://github.com/WebizenAI/webizen-app-docs'
  },
  docsRepositoryBase: 'https://github.com/WebizenAI/webizen-app-docs/tree/main',
  search: {
    provider: 'pagefind'
  },
  footer: {
    text: `WebizenAI Â© ${new Date().getFullYear()}`
  },
  sidebar: {
    defaultMenuCollapseLevel: 1,
    toggleButton: true
  },
  toc: {
    float: true
  },
  basePath: '/webizen-app-docs'
};

export default {
  content: [
    './pages/**/*.{js,jsx,md,mdx}',
    './components/**/*.{js,jsx}',
    './theme.config.mjs'
  ],
  theme: { extend: {} },
  plugins: []
};

